    top: 0;
    left: 18%;
    height: 100%;
    width: 25rem;
    background-color: $color-primary;

    z-index: 1;
  }

  .title {
    text-align: center;
    font-size: 5rem;
    text-transform: uppercase;
    letter-spacing: 2px;
    font-weight: 600;
    margin-bottom: 5rem;
  }

  &__images {
    display: flex;
    height: 50vh;

    .img-1 {
      width: 50%;
      transition: all 0.4s ease;
      z-index: 10;
    }
    .img-2 {
      width: 25%;
      transition: all 0.4s ease;
    }
    .img-3 {
      width: 25%;
      transition: all 0.4s ease;
    }

    .img-1:hover,
    .img-2:hover,
    .img-3:hover {
