      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-top: 5rem;
    }

    &__text {
      width: 50%;
      font-size: 2.4rem;
      font-family: 'Lato', sans-serif;
      color: #fff;
      line-height: 1.5;
      padding: 0 9rem;
      z-index: 10;
    }

    &__socials {
      font-size: 2.4rem;
      color: #fff;
      width: 50%;
      display: flex;
      flex-direction: column;
      align-items: center;

      .social-media {
        margin-bottom: 5rem;
      }

      .title {
