sources.sass/_breakpoints.scss:
@use 'variables' as *;

@media only screen and (max-width: 87.5em) {
  .gallery {
    &__band {
      opacity: 0.5;
    }

    .infos {
      width: 95%;
    }

    &__images {
      height: auto;
    }
  }

  .new {
    &__band {
      left: 2%;
    }

    &__upper {
      &-text {
        width: 100%;
      }

      &-img {
        width: 40%;
      }
      .wrapper {
        margin-left: 0;
        margin-right: auto;
        width: 60%;
      }
    }
    &__bottom {
      .wrapper {
        width: 90%;

        .item {
          .title {
            font-size: 2.2rem;
          }
          .text {
            font-size: 1.8rem;
          }
        }
      }
    }
  }

  .masterclass {
    &__container {
      .button {
        .btn {
          font-size: 2.4rem;
          padding: 1.5rem 25rem 1.5rem 2rem;
        }
      }
    }
    &__card {
      width: 60%;

      &-title,
      &-subtitle {
        font-size: 3.2rem;
      }
    }

    .price-time {
      .price,
      .time {
        font-size: 2.4rem;
      }
    }
  }
}

@media only screen and (max-width: 62.5em) {
  .gallery {
    &__band {
      display: none;
    }

    &__images {
      height: auto;
    }

    .infos {
      width: 70rem;

      .content {
        flex-direction: column;
        flex-wrap: wrap;
      }

      &__text {
        width: 100%;
        padding: 0 1rem;
        text-align: center;
        margin-bottom: 6rem;
      }

      &__socials {
        text-align: center;
      }
    }
  }

  .new {
    &__upper {
      .wrapper {
        width: 100%;
        justify-content: center;
      }

      &-text {
        width: 60%;
      }
      &-img {
        display: none;
      }
    }
  }

  .menu {
    .container-dishes {
      width: 90%;
      margin: 14rem auto 0 auto;
    }

    .wrapper-especials {
      padding-left: 4rem;
      width: auto;
    }
  }

  .masterclass {
    &__container {
      h3 {
        margin-bottom: 5rem;
      }
    }
  }
}

@media only screen and (max-width: 56.25em) {
  .header {
    .burger {
      display: block;
    }

    &__text {
      flex-direction: column;

      h1 {
        font-size: 6rem;
        width: 40rem;
      }

      a {
        margin-top: 2rem;
        align-self: center;
      }
    }

    .main-nav {
      &__item {
        display: none;
      }
    }
  }

  .concept {
    .text,
    .title,
    &__nav {
      width: 90%;
      text-align: center;
    }

    &__nav {
      flex-wrap: wrap;
      justify-content: center;

      a {
        margin: 1rem;
      }
    }
  }

  .new {
    &__band {
      display: none;
    }

    &__bottom {
      height: auto;
    }

