sources.sass/_header.scss:
@use 'variables' as *;

.header {
  color: #fff;
  background: url(../img/hero-bg-min.jpg);
  background-position: top;
  background-repeat: no-repeat;
  background-size: cover;
  height: 100vh;
  position: relative;

  .burger {
    position: absolute;
    width: 4rem;
    height: 3px;
    background-color: $color-primary;
    top: 4rem;
    right: 2rem;
    display: none;

    &::before,
    &::after {
      content: '';
      width: 4rem;
      height: 3px;
      background-color: $color-primary;
      top: -1rem;
      right: 0rem;
      position: absolute;
    }

    &::after {
      top: 1rem;
    }
  }

  .logo-band {
    width: 27%;
    height: 6rem;
    background-color: $color-primary;
    position: absolute;
    top: 3rem;
    left: 0;
    z-index: 1;
  }

  .container {
    max-width: 120rem;
    margin: 0 auto;
  }

  .main-nav {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding-top: 3rem;
    position: relative;
    &__logo {
      display: flex;
      justify-content: flex-end;

      padding: 1rem;
      font-size: 3rem;
      font-weight: lighter;
      z-index: 10;

      span {
        font-weight: 900;
      }
    }

    &__list {
      display: flex;
      list-style: none;
      flex-wrap: wrap;
    }

    &__item {
      margin: 0 1rem;
      text-align: center;

      a {
        font-size: 3rem;
        padding: 1rem 2rem;
        text-decoration: none;
        color: #fff;
      }

      a:hover {
        color: $color-primary;
      }
    }
  }

  .band-mid {
    width: 100%;
    height: 18rem;
    background-color: $color-primary;
    position: absolute;
    top: 52%;
    transform: translateY(-40%);
    z-index: 1;
  }

  &__text {
    color: #fff;

    display: flex;
    align-items: center;
    justify-content: space-evenly;
    line-height: 1;
    padding-top: 2rem;
    margin: 0 auto;

    h1 {
      width: 55rem;
      font-size: 10rem;
      font-weight: 300;
      text-transform: uppercase;
      letter-spacing: 1rem;

      span {
        font-weight: 900;
        letter-spacing: normal;
      }
    }

    a {
      border: 2px solid #fff;
      align-self: flex-end;
    }

    h1,
    a {
      z-index: 10;
    }
  }
}

