sources.sass/_masterclass.scss:
@use 'variables' as *;

.masterclass {
  background: url(../img/masterclass-bg-min.jpg);
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;

  font-family: 'Lato', sans-serif;

  &__container {
    width: 80%;
    margin: 0 auto;
    padding: 8rem 0;

    h3 {
      font-size: 4rem;
      text-transform: uppercase;
      text-align: right;
      color: #fff;
      margin-bottom: 10rem;
    }

    .button {
      display: flex;
      justify-content: flex-end;

      .btn {
        padding: 3.5rem 25rem 1.5rem 2rem;
      }
    }
  }

  &__card {
    font-size: 3rem;
    color: #fff;
    width: 40%;

    margin-bottom: 5rem;
    margin-left: auto;

    background-color: $color-primary;
    padding: 7rem 6rem;

    &-title {
      font-size: 4.5rem;
      line-height: 1.2;
      text-transform: uppercase;
      font-weight: 600;
      margin-bottom: 2rem;
    }

    &-subtitle {
      font-size: 5rem;
      font-weight: 600;
      margin-bottom: 7rem;
    }

    .price-time {
      align-self: flex-end;
      text-align: right;

      span {
        font-weight: 600;
      }
      .price {
        text-transform: uppercase;
        margin-bottom: 1rem;
      }
    }
  }
}

sources.sass/_gallery.scss:
@use 'variables' as *;

.gallery {
  background-color: #242424;
  padding: 10rem 0;
  position: relative;

  &__band {
    position: absolute;
    top: 0;
    left: 18%;
    height: 100%;
    width: 25rem;
    background-color: $color-primary;

    z-index: 1;
  }

  .title {
    text-align: center;
    font-size: 5rem;
    text-transform: uppercase;
    letter-spacing: 2px;
    font-weight: 600;
    margin-bottom: 5rem;
  }

  &__images {
    display: flex;
    height: 50vh;

    .img-1 {
      width: 50%;
      transition: all 0.4s ease;
      z-index: 10;
    }
    .img-2 {
      width: 25%;
      transition: all 0.4s ease;
    }
    .img-3 {
      width: 25%;
      transition: all 0.4s ease;
    }

    .img-1:hover,
    .img-2:hover,
    .img-3:hover {
      transform: scale(0.95);
      border: 5px solid $color-primary;
    }
  }

  .infos {
    width: 70%;
    margin: 10rem auto;

    h3 {
      font-family: 'Montserrat', sans-serif;
      font-size: 4rem;
      color: #fff;
      text-align: center;
      z-index: 10;
    }

    .content {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-top: 5rem;
    }

    &__text {
      width: 50%;
      font-size: 2.4rem;
      font-family: 'Lato', sans-serif;
      color: #fff;
      line-height: 1.5;
      padding: 0 9rem;
      z-index: 10;
    }

    &__socials {
      font-size: 2.4rem;
      color: #fff;
      width: 50%;
      display: flex;
      flex-direction: column;
      align-items: center;

      .social-media {
        margin-bottom: 5rem;
      }

      .title {
        font-size: 2.4rem;
        text-transform: lowercase;
      }

      .icons img {
        height: 5rem;
        margin-right: 1rem;
      }

      .icons img:hover {
        cursor: pointer;
      }

      p {
        margin-bottom: 1rem;
      }
    }

    .website .title:hover {
      cursor: pointer;
    }
  }
}

